import React, { Component } from 'react';

class Login extends Component {

  constructor(props){
    super(props);
    this.state={
      answer: ''
    }
    this.inputAnswer = this.inputAnswer.bind(this);
    this.submit = this.submit.bind(this);
  }
  inputAnswer (event) {
    this.setState({
        answer: event.target.value
    })
  }

  submit (event) {
    console.log('Answer: ' + this.state.answer);
    //event.preventDefault();
    if(this.state.answer === 'test'){
      return{
        backgroundColor: 'green'
      }
    }
  }

  render() {
    return (
      <div style={formStyle}>
        <form onSubmit={this.submit} style={inputStyle}>
              <label style={{  }}>Who hosted the 2018 Polish Campout</label>
              <input type="text" value={this.state.answer} onChange={this.inputAnswer.bind(this)}/>
              <input type="submit" value="Submit"/>
          </form>
      </div>
    );
  }
}

const formStyle = {
  backgroundColor: 'white',
  width: '90%',
  height: '45%',
  margin: 'auto',
  borderRadius: '25px',
  display: 'flex',
  boxShadow: '1px 1.5px #888',
  border: '2px solid black'
}
const inputStyle = {
  width: '75%',
  margin: 'auto',
  padding: '15px',
  display: 'flex',
  border: '2px solid black'
}

export default Login;
