import React, { Component } from 'react';

export class SeePrefs extends Component {

  constructor(props){
    super(props);
    this.state={
      name: ''
    }
    this.handleChange = this.inputName.bind(this);
    this.submit = this.submit.bind(this);
  }
  inputName (event) {
    this.setState({
        Name: event.target.value
    })
  }

  submit (event) {
    console.log('Name: ' + this.state.Name);
    //event.preventDefault();


  }
  //http://localhost:8888/backend/mysql.php
  render() {
    return (
      <div>
        <div>
            <form className="home-form"
                    id="form2"
                    method="get"
                    name="who"
                    action=""
                    onSubmit={this.submit} >
                <p className="spacer"><span className="preferences">I want to see..</span></p>
                <select value={this.state.value} onChange={this.handleChange}>
                    <option value='' ></option>
                    <option value={this.state.answer} >Dale's</option>
                    <option value="Justin">Justin's</option>
                    <option value="Steven">Steven's</option>
                    <option value="Sam">Sam's</option>
                </select>
                <p className="spacer"><span className="preferences">preferred items...</span></p>

                <button type="submit" className="login-btn" id="mains-btns" >Show me!</button>
                <div className="show-results"></div>
                <div className="show-results1"></div>
            </form>
        </div>
      </div>
    )
  }
}

export default SeePrefs
