import React, { Component } from 'react';

export class SeePrefs extends Component {

  constructor(props){
    super(props);
    this.state = {
      // pref1: '',
      // pref2: '',
      // pref3: ''
      preferences: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.submit = this.submit.bind(this);
  }
  handleChange (event) {
    this.setState({
        Name: event.target.value
    })
  }

  submit (event) {
    fetch('http://localhost:8888/mysql.php')
    .then(res => res.json())
    .then(preferences => this.setState({preferences: preferences})
      // this.setState({ 
      // name: preferences.name,
      // pref1: preferences.pref1,
      // pref2: preferences.pref2,
      // pref3: preferences.pref3
    );
    event.preventDefault();
    //console.log(this.state.Name);
    if(this.state.Name === 'Dale'){
      console.log('Dale is seen');
    }
    else if(this.state.Name === 'Justin'){
      console.log('Justin is seen');
    }
    else if(this.state.Name === 'Steve'){
      console.log('Justin is seen');
    }
    else if(this.state.Name === 'Justin'){
      console.log('Justin is seen');
    }
  }

  handleSubmit (event) {
    //console.log("test");

  }

  componentWillMount(){

  }
  componentDidMount(){


  }

  render() {
    // var myName = this.state.preferences.name;
    const personPrefs = this.state.preferences.map(preferences => (
      <div key={preferences.name}>
        <h2>{preferences.name} wants..</h2>
        <p>{preferences.pref1}</p>
        <p>{preferences.pref2}</p>
        <p>{preferences.pref3}</p>
      </div>
    ));

    return (
      <div>
        <div className="home-form">
            <form id="form2"
                    method="get"
                    action="http://localhost:8888/mysql.php"
                    onSubmit={this.submit} >
                <p className="spacer"><span className="preferences">I want to see..</span></p>
                <select value={this.state.value} onChange={this.handleChange} name="name">
                    <option value='' ></option>
                    <option value="Dale">Dale's</option>
                    <option value="Justin">Justin's</option>
                    <option value="Steven">Steven's</option>
                    <option value="Sam">Sam's</option>
                </select>
                <p className="spacer"><span className="preferences">preferred items...</span></p>

                <button type="submit" className="login-btn" id="mains-btns" onSubmit={this.submit}>Show me!</button>
                <div className="show-results"></div>
                <div className="show-results1"></div>
                {personPrefs}
            </form>
        </div>
      </div>
    )
  }
}

export default SeePrefs

